package com.qlk.test;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.net.URL;
import java.net.URLConnection;
import java.util.List;
import java.util.Map;

public class Request {
	private String[] requestRow;
	
	public String[] setRequestMethod(int sheetNumber,int rowNumber){
		ExcelReader excelReader=new ExcelReader("C:\\Users\\cbhbit\\Desktop\\ODC.xlsx");
		try {
			requestRow=excelReader.readExcelTitle(sheetNumber, rowNumber);
		} catch (Exception e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		return requestRow;
	}
	
	private String requestURL;
	public String getRequestURL(int rowNumber){
		requestURL=setRequestMethod(0,1)[0]+setRequestMethod(1,rowNumber)[0]+setRequestMethod(1,rowNumber)[1];
		return requestURL;
	}
	
	public String sendGet(int rowNumber){
		String result = "";
		String url=getRequestURL(rowNumber);
		String param=setRequestMethod(1,rowNumber)[2];
        BufferedReader in = null;
        String urlNameString;
        try {
        	if(param=="")
        		urlNameString = url;
        	else
                urlNameString = url + "?" + param;
            URL realUrl = new URL(urlNameString);
            //System.out.println(urlNameString);
            // 打开和URL之间的连接
            URLConnection connection = realUrl.openConnection();
            // 设置通用的请求属性
            connection.setRequestProperty("accept", "*/*");
            connection.setRequestProperty("connection", "Keep-Alive");
            connection.setRequestProperty("user-agent",
                    "Mozilla/4.0 (compatible; MSIE 6.0; Windows NT 5.1;SV1)");
            // 建立实际的连接
            connection.connect();
            /*// 获取所有响应头字段
            Map<String, List<String>> map = connection.getHeaderFields();
            // 遍历所有的响应头字段
            for (String key : map.keySet()) {
                System.out.println(key + "--->" + map.get(key));
            }*/
            // 定义 BufferedReader输入流来读取URL的响应
            in = new BufferedReader(new InputStreamReader(
                    connection.getInputStream()));
            String line;
            while ((line = in.readLine()) != null) {
                result += line;
            }
        } catch (Exception e) {
            System.out.println("发送GET请求出现异常！" + e);
            e.printStackTrace();
        }
        // 使用finally块来关闭输入流
        finally {
            try {
                if (in != null) {
                    in.close();
                }
            } catch (Exception e2) {
                e2.printStackTrace();
            }
        }
        return result;
	}

	public String sendPost(int rowNumber) {
	        PrintWriter out = null;
	        String url=getRequestURL(rowNumber);
			String param=setRequestMethod(1,rowNumber)[2];
	        BufferedReader in = null;
	        String result = "";
	        String urlNameString;
	        try {	        	
	        	urlNameString = url;
	        	
	            URL realUrl = new URL(urlNameString);
	            // 打开和URL之间的连接
	            URLConnection conn = realUrl.openConnection();
	            // 设置通用的请求属性
	            conn.setRequestProperty("accept", "*/*");
	            conn.setRequestProperty("connection", "Keep-Alive");
	            conn.setRequestProperty("user-agent",
	                    "Mozilla/4.0 (compatible; MSIE 6.0; Windows NT 5.1;SV1)");
	            conn.setRequestProperty("Content-Type", "application/json");
	            // 发送POST请求必须设置如下两行
	            conn.setDoOutput(true);
	            conn.setDoInput(true);
	            // 获取URLConnection对象对应的输出流
	            out = new PrintWriter(conn.getOutputStream());
	            // 发送请求参数
	            out.print(param);
	            // flush输出流的缓冲
	            out.flush();
	            // 定义BufferedReader输入流来读取URL的响应
	            in = new BufferedReader(
	                    new InputStreamReader(conn.getInputStream()));
	            String line;
	            while ((line = in.readLine()) != null) {
	                result += line;
	            }
	        } catch (Exception e) {
	            System.out.println("发送 POST 请求出现异常！"+e);
	            e.printStackTrace();
	        }
	        //使用finally块来关闭输出流、输入流
	        finally{
	            try{
	                if(out!=null){
	                    out.close();
	                }
	                if(in!=null){
	                    in.close();
	                }
	            }
	            catch(IOException ex){
	                ex.printStackTrace();
	            }
	        }
	        return result;
	    }
}
